mapper.update:
UPDATE moment SET
title = #{moment.title},
location = #{moment.location},
eventTime = #{moment.eventTime},
updateTime = #{moment.updateTime}
WHERE id = #{id}

UPDATE moment SET
isDeleted = TRUE
WHERE id = #{id}

mapper.select:
SELECT * FROM moment
WHERE isDeleted = FALSE and eventTime < #{timeCursor}
ORDER BY eventTime DESC, updateTime DESC LIMIT #{size}
